// Superclass: BankAccountclass BankAccount {    public String accountNumber;    protected String accountHolder;    private double balance;    // Constructor    public BankAccount(String accountNumber, String accountHolder, double balance) {        this.accountNumber = accountNumber;        this.accountHolder = accountHolder;        this.balance = balance;    }    // Public method to deposit amount    public void deposit(double amount) {        if (amount > 0) {            balance += amount;            System.out.println("Deposited: $" + amount);        } else {            System.out.println("Invalid deposit amount.");        }    }    // Public method to withdraw amount    public void withdraw(double amount) {        if (amount > 0 && amount <= balance) {            balance -= amount;            System.out.println("Withdrawn: $" + amount);        } else {            System.out.println("Invalid withdraw amount.");        }    }    // Public method to get balance    public double getBalance() {        return balance;    }    // Display Bank Account details    public void displayAccountDetails() {        System.out.println("Account Number: " + accountNumber);        System.out.println("Account Holder: " + accountHolder);        System.out.println("Balance: $" + balance);    }}// Subclass: SavingsAccountclass SavingsAccount extends BankAccount {    private double interestRate;    // Constructor    public SavingsAccount(String accountNumber, String accountHolder, double balance, double interestRate) {        super(accountNumber, accountHolder, balance);        this.interestRate = interestRate;    }    // Display Savings Account details    public void displaySavingsDetails() {        // Accessing public and protected members directly        System.out.println("Savings Account Number: " + accountNumber);        System.out.println("Account Holder: " + accountHolder);        System.out.println("Balance: $" + getBalance());        System.out.println("Interest Rate: " + interestRate + "%");    }}// Main class to testpublic class BankSystem {    public static void main(String[] args) {        // Creating BankAccount object        BankAccount ba = new BankAccount("BA123", "Alice", 5000.00);        ba.displayAccountDetails();        System.out.println();        // Modify balance        ba.deposit(1500);        ba.withdraw(2000);        System.out.println("Updated Balance: $" + ba.getBalance());        System.out.println();        // Creating SavingsAccount object        SavingsAccount sa = new SavingsAccount("SA456", "Bob", 10000.00, 4.5);        sa.displaySavingsDetails();    }}