// Superclass: Bookclass Book {    public String ISBN;       // public    protected String title;   // protected    private String author;    // private    // Constructor    public Book(String ISBN, String title, String author) {        this.ISBN = ISBN;        this.title = title;        this.author = author;    }    // Public method to set author    public void setAuthor(String author) {        this.author = author;    }    // Public method to get author    public String getAuthor() {        return author;    }    // Display book details    public void displayBookDetails() {        System.out.println("ISBN: " + ISBN);        System.out.println("Title: " + title);        System.out.println("Author: " + author);    }}// Subclass: EBookclass EBook extends Book {    private double fileSizeMB;    // Constructor    public EBook(String ISBN, String title, String author, double fileSizeMB) {        super(ISBN, title, author);        this.fileSizeMB = fileSizeMB;    }    // Display eBook details    public void displayEBookDetails() {        // Accessing public ISBN directly        System.out.println("E-Book ISBN: " + ISBN);        // Accessing protected title directly        System.out.println("E-Book Title: " + title);        // Accessing private author via public getter        System.out.println("E-Book Author: " + getAuthor());        System.out.println("File Size: " + fileSizeMB + " MB");    }}// Main class to testpublic class Books {    public static void main(String[] args) {        // Creating a Book object        Book book = new Book("123-456-789", "Java Programming", "James Gosling");        book.displayBookDetails();        System.out.println();        // Modifying author name using setter        book.setAuthor("Updated Author");        System.out.println("Updated Author: " + book.getAuthor());        System.out.println();        // Creating an EBook object        EBook ebook = new EBook("987-654-321", "Advanced Java", "John Doe", 5.2);        ebook.displayEBookDetails();    }}