// Base class: Studentclass Student {    public int rollNumber;     // public    protected String name;     // protected    private double CGPA;       // private    // Constructor    public Student(int rollNumber, String name, double CGPA) {        this.rollNumber = rollNumber;        this.name = name;        this.CGPA = CGPA;    }    // Public getter for CGPA    public double getCGPA() {        return CGPA;    }    // Public setter for CGPA    public void setCGPA(double CGPA) {        if (CGPA >= 0.0 && CGPA <= 10.0) {            this.CGPA = CGPA;        } else {            System.out.println("Invalid CGPA value");        }    }    // Display student details    public void displayDetails() {        System.out.println("Roll Number: " + rollNumber);        System.out.println("Name: " + name);        System.out.println("CGPA: " + CGPA);    }}// Subclass: PostgraduateStudentclass PostgraduateStudent extends Student {    private String specialization;    // Constructor    public PostgraduateStudent(int rollNumber, String name, double CGPA, String specialization) {        super(rollNumber, name, CGPA);        this.specialization = specialization;    }    // Display PostgraduateStudent details    public void displayPostgraduateDetails() {        // Can access protected 'name' directly        System.out.println("Postgraduate Student Name: " + name);        System.out.println("Specialization: " + specialization);        System.out.println("CGPA: " + getCGPA());    }}// Main class to testpublic class Students {    public static void main(String[] args) {        // Creating a Student object        Student student = new Student(101, "Alice", 8.7);        student.displayDetails();        // Modify CGPA        student.setCGPA(9.2);        System.out.println("Updated CGPA: " + student.getCGPA());        System.out.println();        // Creating a PostgraduateStudent object        PostgraduateStudent pgStudent = new PostgraduateStudent(201, "Bob", 9.5, "Data Science");        pgStudent.displayPostgraduateDetails();    }}